def nb_journaliste
	nb_journaliste = @journalist.count
	puts "Il y #{@journalist.count} journalist."
end


def handle_num_nb
	handle_num_nb = @journalist.find_all {|obj| obj =~/[0123456789]/ }
	puts "Il y a #{handle_num_nb.count} handler avec un numéro."
end


def handle_Aude
	handle_Aude = @journalist.grep (/[Aa]ude/)
	puts "Il y a #{handle_Aude.count} handler avec les lettre Aude."
end


def handle_maj
	handle_maj = @journalist.find_all {|obj| obj =~ /@[A-Z]/ }
	puts "Il y a #{handle_maj.count} handler qui commece par une majuscule."
end

def com_maj
	com_maj = @journalist.find_all {|obj| obj =~/[AZERTYUIOPQSDFGHJKLMWXCVBN]/}
	puts "Il y a #{com_maj.count} qui contient une majuscule."
end

def underscore
	underscore = @journalist.join
	puts "Il y a #{underscore.count('_')} dans toutes la liste."
end

def ordre_alpha
	ordre_alpha = @journalist.sort_by {|alpha| alpha.downcase}
	puts "Ordre alphabetique de la liste des journalistes :"
	puts ordre_alpha.inspect
end

def handle_court
	handle_court = @journalist.sort_by(&:length)
	puts "Voici les 50 handle les plus cours :"
	puts handle_court[0..49].inspect
end

def index_epenser
	index_epenser = @journalist.index('@epenser')
	puts "#{index_epenser}"
end 



def choix



	puts "Que voulez-vous savoir?"
	puts "1.Le nombre total des journalistes dans le tableau."
	puts "2.Le nombre des handle qui contiennent un numéro."
	puts "3.Le nombre des handle qui contiennent les 4 lettres du prénom Aude à la suite."
	puts "4.Le nombre des handle qui commencent par une majuscule"
	puts "5.Le nombre des handle qui contiennent une majuscule."
	puts "6.Le nombre des underscores _ dans tous les pseudos confondus."
	puts "7.La la liste de handle par ordre alphabétique."
	puts "8.Les 50 handles les plus courts."
	puts "9.la position de @epenser dans l'array de la personne."

	n = gets.chomp.to_i
	if n == 1
		nb_journaliste
		elsif n == 2
			handle_num_nb
		elsif n == 3
			handle_Aude
		elsif n == 4
			handle_maj
		elsif n == 5
			com_maj
		elsif n == 6
			underscore
		elsif n == 7
			ordre_alpha
		elsif n == 8
			handle_court
		else n == 9
			index_epenser
		end
end


